import * as types from "../../actionTypes";
import AssetApi from "../../../api/AssetApi";

let actionsFunction = {

  //CRUD METHODS

  // Delete asset
  deleteAsset: function(id) {
    return function(dispatch) {
      return AssetApi
        .deleteAsset(id)
        .then(asset => {
          dispatch(actionsFunction.deleteAssetSuccess(asset));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteAssetSuccess: function(asset) {
    return { type: types.DELETE_ASSET_SUCCESS, payload: asset };
  },

  // Load  list
  loadAssetList: function() {
    return function(dispatch) {
      return AssetApi
        .getAssetList()
        .then(list => {
          dispatch(actionsFunction.loadAssetListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadAssetListSuccess: function(list) {
    return { type: types.LIST_ASSET_SUCCESS, payload: list };
  },


};

export default actionsFunction;
